initialize.fn:
function(instance, properties, context) {
console.log('Inicializando elemento Hola Mundo');
instance.data.message = 'Hola Mundo desde mi plugin!';
instance.canvas.html('<div style="padding: 20px; border: 2px solid blue; text-align: center;">Hola Mundo</div>');
console.log('Elemento inicializado');
}

preview.fn:
function(instance, properties, context) {
instance.canvas.html('<div style="padding: 20px; border: 2px dashed gray; text-align: center; color: gray;">Hola Mundo - Preview</div>');
}

reset.fn:
function(instance, properties, context) {
console.log('Reiniciando elemento');
instance.data = {};
instance.canvas.empty();
}

update.fn:
function(instance, properties, context) {
console.log('Actualizando elemento');
if (properties.mensaje_personalizado) {
    instance.canvas.find('div').text(properties.mensaje_personalizado);
}
}
